import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { makeStyles, withStyles } from '@material-ui/core/styles'
import clsx from 'clsx'
import Stepper from '@material-ui/core/Stepper'
import Step from '@material-ui/core/Step'
import StepLabel from '@material-ui/core/StepLabel'
import Check from '@material-ui/icons/Check'
import SettingsIcon from '@material-ui/icons/Settings'
import CloseIcon from '@material-ui/icons/Close'
import GroupAddIcon from '@material-ui/icons/GroupAdd'
import VideoLabelIcon from '@material-ui/icons/VideoLabel'
import StepConnector from '@material-ui/core/StepConnector'
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'

import Dialog from '@material-ui/core/Dialog'
import DialogContent from '@material-ui/core/DialogContent'
import TextField from '@material-ui/core/TextField'
import DialogTitle from '@material-ui/core/DialogTitle'
import Slide from '@material-ui/core/Slide'
import './statics/css/Popup.scss'

const Transition = React.forwardRef(function Transition (props, ref) {
    return <Slide direction="up" ref={ref} {...props} />
})

const useQontoStepIconStyles = makeStyles({
    root: {
        color: '#eaeaf0',
        display: 'flex',
        height: 22,
        alignItems: 'center'
    },
    active: {
        color: '#784af4'
    },
    circle: {
        width: 8,
        height: 8,
        borderRadius: '50%',
        backgroundColor: 'currentColor'
    },
    completed: {
        color: '#784af4',
        zIndex: 1,
        fontSize: 18
    }
})

function QontoStepIcon (props) {
    const classes = useQontoStepIconStyles()
    const { active, completed } = props

    return (
        <div
            className={clsx(classes.root, {
                [classes.active]: active
            })}
        >
            {completed ? <Check className={classes.completed} /> : <div className={classes.circle} />}
        </div>
    )
}

QontoStepIcon.propTypes = {
    /**
   * Whether this step is active.
   */
    active: PropTypes.bool,
    /**
   * Mark the step as completed. Is passed to child components.
   */
    completed: PropTypes.bool
}

const ColorlibConnector = withStyles({
    alternativeLabel: {
        top: 22
    },
    active: {
        '& $line': {
            backgroundImage:
        'linear-gradient( 95deg,rgb(242,113,33) 0%,rgb(233,64,87) 50%,rgb(138,35,135) 100%)'
        }
    },
    completed: {
        '& $line': {
            backgroundImage:
        'linear-gradient( 95deg,rgb(242,113,33) 0%,rgb(233,64,87) 50%,rgb(138,35,135) 100%)'
        }
    },
    line: {
        height: 3,
        border: 0,
        backgroundColor: '#eaeaf0',
        borderRadius: 1
    }
})(StepConnector)

const useColorlibStepIconStyles = makeStyles({
    root: {
        backgroundColor: '#ccc',
        zIndex: 1,
        color: '#fff',
        width: 50,
        height: 50,
        display: 'flex',
        borderRadius: '50%',
        justifyContent: 'center',
        alignItems: 'center'
    },
    active: {
        backgroundImage:
      'linear-gradient( 136deg, rgb(242,113,33) 0%, rgb(233,64,87) 50%, rgb(138,35,135) 100%)',
        boxShadow: '0 4px 10px 0 rgba(0,0,0,.25)'
    },
    completed: {
        backgroundImage:
      'linear-gradient( 136deg, rgb(242,113,33) 0%, rgb(233,64,87) 50%, rgb(138,35,135) 100%)'
    }
})

function ColorlibStepIcon (props) {
    const classes = useColorlibStepIconStyles()
    const { active, completed } = props

    const icons = {
        1: <SettingsIcon />,
        2: <GroupAddIcon />,
        3: <VideoLabelIcon />
    }

    return (
        <div
            className={clsx(classes.root, {
                [classes.active]: active,
                [classes.completed]: completed
            })}
        >
            {icons[String(props.icon)]}
        </div>
    )
}

ColorlibStepIcon.propTypes = {
    /**
   * Whether this step is active.
   */
    active: PropTypes.bool,
    /**
   * Mark the step as completed. Is passed to child components.
   */
    completed: PropTypes.bool,
    /**
   * The label displayed in the step icon.
   */
    icon: PropTypes.node
}

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%'
    },
    button: {
        marginRight: theme.spacing(1)
    },
    instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1)
    }
}))

function getSteps () {
    return ['Contact', 'Coordonnés', 'Facturation']
}

function getStepContent (step) {
    switch (step) {
    case 0:
        return 'Select campaign settings...'
    case 1:
        return 'What is an ad group anyways?'
    case 2:
        return 'This is the bit I really care about!'
    default:
        return 'Unknown step'
    }
}

export function CustomizedSteppers () {
    const [state, setState] = useState({
        Prenom: '',
        Nom: '',
        Email: '',
        Tel: ''
    })

    const classes = useStyles()
    const [activeStep, setActiveStep] = React.useState(0)
    const steps = getSteps()

    const handleNext = () => {
        setActiveStep((prevActiveStep) => prevActiveStep + 1)
    }

    const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1)
    }

    const handleReset = () => {
        setActiveStep(0)
    }

    const onChangeInput = (e) => {
        setState(Object.assign({}, state, { [e.target.name]: e.target.value }))
    }

    return (
        <div className={classes.root}>
            {/* <Stepper alternativeLabel activeStep={activeStep}> */}
            {/*     {steps.map((label) => ( */}
            {/*         <Step key={label}> */}
            {/*             <StepLabel>{label}</StepLabel> */}
            {/*         </Step> */}
            {/*     ))} */}
            {/* </Stepper> */}
            {/* <Stepper alternativeLabel activeStep={activeStep} connector={<QontoConnector />}> */}
            {/*     {steps.map((label) => ( */}
            {/*         <Step key={label}> */}
            {/*             <StepLabel StepIconComponent={QontoStepIcon}>{label}</StepLabel> */}
            {/*         </Step> */}
            {/*     ))} */}
            {/* </Stepper> */}
            <Stepper
                alternativeLabel
                activeStep={activeStep}
                connector={<ColorlibConnector />}
            >
                {steps.map((label) => (
                    <Step key={label}>
                        <StepLabel
                            StepIconComponent={ColorlibStepIcon}>{label}</StepLabel>
                    </Step>
                ))}
            </Stepper>
            <div>
                <div className='TextFieldPopup'>
                    <h1>Qui demande cette formation ?</h1>
                    <TextField
                        label="Prénom*"
                        value={state.Prenom}
                        onChange={onChangeInput}
                        variant="outlined"
                        margin="dense"
                        name="Prenom"
                    />
                    <TextField
                        label="Nom*"
                        value={state.Nom}
                        onChange={onChangeInput}
                        margin="dense"
                        variant="outlined"
                        name="Nom"
                    />
                    <TextField
                        label="Email*"
                        value={state.Email}
                        onChange={onChangeInput}
                        margin="dense"
                        variant="outlined"
                        name="Email"
                    />
                    <TextField
                        label="Téléphone*"
                        value={state.Tel}
                        onChange={onChangeInput}
                        margin="dense"
                        variant="outlined"
                        name="Tel"
                    />
                </div>
                <div className='bottomButtonPopup'>
                    {activeStep === steps.length ? (
                        <div>
                            <Typography className={classes.instructions}>
                          All steps completed - you&apos;re finished
                            </Typography>
                            <Button onClick={handleReset} className={classes.button}>
              Reset
                            </Button>
                        </div>
                    ) : (
                        <div>
                            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
                            <div>
                                <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                Back
                                </Button>
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={handleNext}
                                    className={classes.button}
                                >
                                    {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
                                </Button>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}

export default function Popup () {
    const [open, setOpen] = React.useState(false)

    const handleClickOpen = () => {
        setOpen(true)
    }

    const handleClose = () => {
        setOpen(false)
    }

    return (
        <div className='Popup'>
            <Button
                variant="contained"
                color="primary"
                onClick={handleClickOpen}
            >
                Demander un devis sur la formation Excel 2003 Niveau 1 :
                Initiation - Débutant - Les bases
            </Button>
            <Dialog
                open={open}
                TransitionComponent={Transition}
                keepMounted
                onClose={handleClose}
                aria-labelledby="alert-dialog-slide-title"
                aria-describedby="alert-dialog-slide-description"
            >
                <div className='headerPopup'>
                    <div></div>
                    <DialogTitle id="alert-dialog-slide-title">
                        Demander un devis
                    </DialogTitle>
                    <div
                        className='closePopup'
                        onClick={handleClose}
                    >
                        <CloseIcon/>
                    </div>
                </div>
                <DialogContent>
                    <CustomizedSteppers/>
                </DialogContent>
            </Dialog>
        </div>
    )
}
