import { ON_CHANGE } from './actions/typesContacts'

const initState = {
    datas: {
        Prenom: '',
        Nom: '',
        Email: '',
        Tel: ''
    }
}

const reducers = (state = initState, action) => {
    let newState = []
    switch (action.type) {
    case ON_CHANGE:
        newState = [...state.dataAchats, action.data]
        return Object.assign({}, state, newState)
    default:
        return state
    }
}

export default reducers
