import React, { useState, useEffect } from 'react'
import TextField from '@material-ui/core/TextField'
import { useDispatch, useSelector } from 'react-redux'
import Button from '@material-ui/core/Button'
import ButtonGroup from '@material-ui/core/ButtonGroup'
import { makeStyles } from '@material-ui/core/styles'
import UserIcon from '@material-ui/icons/Save'

import InputLabel from '@material-ui/core/InputLabel'
import MenuItem from '@material-ui/core/MenuItem'
import FormControl from '@material-ui/core/FormControl'
import Select from '@material-ui/core/Select'
import pays from './pays'

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        '& > *': {
            margin: theme.spacing(1),
            width: '100%'
        }
    }
}))

function Positions () {
    const classes = useStyles()
    const [pages, setPages] = useState('Particulier')
    const dispatch = useDispatch()
    const state = useSelector(state => state.Positions)

    const onChangeInput = (e) => {
        dispatch({
            type: 'ON_CHANGE_POSITIONS',
            datas: { [e.target.name]: e.target.value }
        })
    }

    useEffect(() => {
        dispatch({
            type: 'ON_CHANGE_POSITIONS',
            datas: { Pays: pays()[0] }
        })
    }, [dispatch])

    return (
        <>
            <h1>Parler Ã  un conseiller: 09 72 65 24 84</h1>
            <div className={classes.root}>
                <ButtonGroup
                    variant="contained"
                    color="primary"
                    aria-label="contained primary button group"
                >
                    <Button
                        variant={
                            pages === 'Particulier'
                                ? 'contained'
                                : 'text'
                        }
                        color='primary'
                        size="large"
                        onClick={() => setPages('Particulier')}
                        className={classes.button}
                        startIcon={<UserIcon />}
                        style={{ flex: 1 }}
                    >Particulier</Button>
                    <Button
                        variant={
                            pages === 'Professionnel'
                                ? 'contained'
                                : 'text'
                        }
                        color='primary'
                        size="large"
                        onClick={() => setPages('Professionnel')}
                        className={classes.button}
                        startIcon={<UserIcon />}
                        style={{ flex: 1 }}
                    >Professionnel</Button>
                </ButtonGroup>
            </div>
                    <div
                        style={{
                            display: 'flex',
                            justifyContent: 'space-between',
                            width: '100%'
                        }}>
                        {
                            pages === 'Particulier' &&
                            <>
                                <TextField
                                    label="Prenom"
                                    value={state.Prenom}
                                    onChange={onChangeInput}
                                    margin="dense"
                                    variant="outlined"
                                    name="Prenom"
                                />
                                <div style={{ width: 10 }}></div>
                                <TextField
                                    label="Nom*"
                                    value={state.Nom}
                                    onChange={onChangeInput}
                                    margin="dense"
                                    variant="outlined"
                                    name="Nom"
                                />
                            </>
                        }
                        {
                            pages === 'Professionnel' &&
                            <TextField
                                label="Entreprise*"
                                value={state.Entreprise}
                                onChange={onChangeInput}
                                margin="dense"
                                variant="outlined"
                                name="Entreprise"
                            />
                        }
                    </div>

        <div
            style={{
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%'
            }}>
            <TextField
                label="Ville*"
                value={state.Ville}
                onChange={onChangeInput}
                variant="outlined"
                margin="dense"
                name="Ville"
            />
            <div style={{ width: 10 }}></div>
            <TextField
                label="Code Postal*"
                value={state.CodePostal}
                onChange={onChangeInput}
                margin="dense"
                variant="outlined"
                name="CodePostal"
            />
        </div>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    width: '100%'
                }}>
                <TextField
                    label="Adresse*"
                    value={state.Adresse}
                    onChange={onChangeInput}
                    margin="dense"
                    variant="outlined"
                    name="Adresse"
                />
                <div style={{ width: 10 }}></div>
                <FormControl
                    variant="outlined"
                    margin="dense"
                >
                    <InputLabel
                        id="demo-simple-select-outlined-label">
                        Pays*
                    </InputLabel>
                    <Select
                        labelId="demo-simple-select-outlined-label"
                        id="demo-simple-select-outlined"
                        value={state.Pays}
                        onChange={onChangeInput}
                        label="Age"
                    >
                        {
                            pays().map(e => <MenuItem key={e} value={e}>{e}</MenuItem>)
                        }
                    </Select>
                </FormControl>
            </div>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    width: '100%'
                }}>
                <TextField
                    label="Message"
                    value={state.Message}
                    onChange={onChangeInput}
                    multiline
                    rows={5}
                    margin="dense"
                    variant="outlined"
                    name="Message"
                />
            </div>
        </>
    )
}

export default Positions
