import React, { useEffect, useState } from 'react'
import TextField from '@material-ui/core/TextField'
import { useDispatch, useSelector } from 'react-redux'

function Contacts () {
    const dispatch = useDispatch()
    const state = useSelector(state => state.Contacts)
    const [required, setReqired] = useState(false)

    useEffect(() => {
        const { Prenom, Nom, Email, Tel } = state
        setReqired(false)
        if (Prenom && Nom && Email && Tel) {
            setReqired(true)
        }
    }, [state])

    const onChangeInput = (e) => {
        dispatch({
            type: 'ON_CHANGE_CONTACTS',
            datas: { [e.target.name]: e.target.value }
        })
    }

    return (
        <>
            <h1>Qui demande cette formation ?</h1>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    width: '100%'
                }}>
                <TextField
                    label="Prénom*"
                    value={state.Prenom}
                    onChange={onChangeInput}
                    variant="outlined"
                    margin="dense"
                    name="Prenom"
                />
                <div style={{ width: 10 }}></div>
                <TextField
                    label="Nom*"
                    value={state.Nom}
                    onChange={onChangeInput}
                    margin="dense"
                    variant="outlined"
                    name="Nom"
                />
            </div>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    width: '100%'
                }}>
                <TextField
                    label="Email*"
                    value={state.Email}
                    onChange={onChangeInput}
                    margin="dense"
                    variant="outlined"
                    name="Email"
                />
                <div style={{ width: 10 }}></div>
                <TextField
                    label="Téléphone*"
                    value={state.Tel}
                    onChange={onChangeInput}
                    margin="dense"
                    variant="outlined"
                    name="Tel"
                />
            </div>
        </>
    )
}

export default Contacts
