import React from 'react'
import './statics/styles/ChatInput.scss'
import {
    Send,
    NavigateNext,
    AttachFile,
    PhotoSizeSelectActual,
    PhotoCamera,
    Videocam,
    KeyboardVoice,
    EmojiEmotions
} from '@material-ui/icons'

import {
    Fab
} from '@material-ui/core'

function ChatInput ({ onSubmit }) {
    const [value, setValue] = React.useState('')
    const [showIcon, setShowIcon] = React.useState(false)
    const [jointe, setJointe] = React.useState('')
    const [image, setImage] = React.useState('')
    const [errorImage, setErrorImage] = React.useState(false)

    const onChange = (e) => {
        setValue(e.target.value)
    }

    const onSend = (e) => {
        e.preventDefault()
        onSubmit(value, jointe, image)
        setValue('')
        setJointe('')
    }

    const OnChangePiece = (e) => {
        const files = e.target.files
        const reader = new FileReader()
        reader.readAsDataURL(files[0])
        reader.onload = (e) => {
            // console.log({ result: e.target.result })
            setJointe(e.target.result)
        }
    }

    const OnChangeInputImage = (e) => {
        const files = e.target.files
        setErrorImage(false)
        console.log(JSON.parse(files[0].type))
        // if (files[0].type.splite('/')[0] === 'image') {
        //     const reader = new FileReader()
        //     reader.readAsDataURL(files[0])
        //     reader.onload = (e) => {
        //     // console.log({ result: e.target.result })
        //         setImage(e.target.result)
        //     }
        // } else {
        //     setErrorImage(true)
        // }
    }

    return (
        <div className='ChatInput'>
            <div
                className='navigate-next vitessWid'
                style={{ width: showIcon ? 220 : 30 }}
            >
                <NavigateNext
                    className='vitesseAnimation'
                    style={{
                        color: 'white',
                        fontSize: 40,
                        transform: 'rotate(' + (showIcon ? 90 : 0) + 'deg)'
                    }}
                    onClick={() => setShowIcon(e => !e)}
                />
                {
                    showIcon && (<div style={{ display: 'flex', alignItems: 'center' }}>
                        <div className='file-input'>
                            <label for="file-input">
                                <AttachFile/>
                            </label>
                            <input
                                type="file"
                                onChange={OnChangePiece}
                                name="ImageChambre"
                                id='file-input'
                            />
                        </div>
                        <div className='file-input'>
                            <label for="file-input-image">
                                <PhotoSizeSelectActual/>
                            </label>
                            <input
                                type="file"
                                error={errorImage}
                                onChange={OnChangeInputImage}
                                name="ImageChambre"
                                id='file-input-image'
                            />
                        </div>

                        <PhotoCamera/>
                        <Videocam/>
                        <KeyboardVoice/>
                    </div>
                    )
                }
            </div>
            <div
                style={{
                    position: 'relative',
                    // width: '90%',
                    flex: 1,
                    display: 'flex',
                    alignItems: 'center'
                }}>
                <input
                    onChange={onChange}
                    value={value}
                    placeholder='Ecrire un message ...'
                />
                <EmojiEmotions
                    style={{
                        color: '#202020',
                        fontSize: 30,
                        position: 'absolute',
                        top: '50%',
                        transform: 'translateY(-50%)',
                        right: '4%',
                        zIndex: 10
                    }}
                />
            </div>
            <Fab aria-label="send" style={{ width: 35, height: 20 }}>
                <Send
                    style={{
                        fontSize: 15,
                        transform: 'rotate(-40deg) translate(1px, 0px)'
                    }}
                    onClick={onSend}
                />
            </Fab>
        </div>
    )
}

export default ChatInput
