import React from 'react'
import './statics/styles/ChatInput.scss'
import {
    Send,
    NavigateNext,
    AttachFile,
    PhotoSizeSelectActual,
    PhotoCamera,
    Videocam
} from '@material-ui/icons'

function ChatInput () {
    const [value, setValue] = React.useState('')
    const [showIcon, setShowIcon] = React.useState(false)

    const onChange = (e) => {
        setValue(e.target.value)
    }

    return (
        <div className='ChatInput'>
            <div
                className='navigate-next vitesseAnimation'
                style={{ width: showIcon ? 320 : 30 }}
            >
                <NavigateNext
                    style={{
                        color: 'white',
                        fontSize: 40,
                        transform: 'rotate(' + (showIcon ? 90 : 0) + 'deg)'
                    }}
                    onClick={() => setShowIcon(e => !e)}
                />
                    {
                        showIcon && (<div style={{display: 'flex', alignItems: 'center'}}>
                            <AttachFile/>
                            <PhotoSizeSelectActual/>
                            <PhotoCamera/>
                            <Videocam/>
                            </div>
                        )
                    }
            </div>
            <input
                onChange={onChange}
                value={value}
            />
            <div className='fab'>
                <Send
                    style={{ fontSize: 15 }}
                />
            </div>
        </div>
    )
}

export default ChatInput
