import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import {
    TextField,
    FormLabel,
    FormControl,
    FormGroup,
    FormControlLabel,
    Checkbox,
    IconButton,
    MenuItem,
    Select,
    Button
} from '@material-ui/core'
import {
    createMuiTheme,
    ThemeProvider
} from '@material-ui/core/styles'

import {
    LocationOn,
    ArrowBack
} from '@material-ui/icons'

class Parlezvous extends Component {
    constructor (props) {
        super(props)
        this.state = {
            error: false,
            isLoading: false,
            Homme: false,
            Femme: false,
            Gays: false,
            Lesbiennes: false,
            Hommebi: false,
            Femmebi: false,
            location: '',
            first: true,
            sexe: 'Homme'
        }
    }

    onSubmit (e) {
        e.preventDefault()
        const {
            email,
            password,
            first
        } = this.state
        if (first) {
            if (email !== '' && password !== '') {
                this.setState({ isLoading: true })
                axios
                    .post('/api/login', {
                        email,
                        password
                    })
                    .then(async ({ data: { token } }) => {
                        this.setState({ isLoading: false })
                        this.setState({ first: false })
                    })
                    .catch(e => {
                        this.setState({ isLoading: false, error: true })
                    })
            }
            console.log(first)
        } else {
            if (email !== '' && password !== '') {
                this.setState({ isLoading: true })
                axios
                    .post('/api/login', {
                        email,
                        password
                    })
                    .then(async ({ data: { token } }) => {
                        this.setState({ isLoading: false })
                        await localStorage.setItem('Token', JSON.stringify(token))
                        this.props.history.push('/Profiles')
                    })
                    .catch(e => {
                        this.setState({ isLoading: false, error: true })
                    })
            }
        }
    }

    onChangeInput (e) {
        this.setState({ [e.target.name]: e.target.value, error: false })
    }

    onChangeCheck (e) {
        this.setState({ [e.target.name]: e.target.checked, error: false })
    }

    initData (val) {
        const tab = []
        if (val === 'dd') {
            for (let i = 1; i < 32; i++) {
                tab.push(i)
            }
        } else if (val === 'mm') {
            const mois = ['janvier', 'février', 'mars', 'avril', 'mai', 'juin', 'juillet', 'août', 'septembre', 'octobre', 'novembre', 'décembre']
            for (let i = 0; i < mois.length; i++) {
                tab.push(mois[i])
            }
        } else {
            const years = new Date().getFullYear()
            for (let i = 0; i < 100; i++) {
                tab.push(years - i)
            }
        }
        return tab
    }

    setReturn () {
        if (this.state.first) {
            this.props.history.push('/Authentification=Inscription')
        } else {
            this.setState({ first: true })
        }
    }

    onClickSexe (e) {
        this.setState({ sexe: e })
    }

    render () {
        const {
            error,
            isLoading,
            Homme,
            Femme,
            Gays,
            Lesbiennes,
            Hommebi,
            Femmebi,
            location,
            first,
            sexe
        } = this.state
        const isHomme = sexe === 'Homme'
        const isFemme = sexe === 'Femme'
        return (
            <div className='Connection'>
                <ThemeProvider>
                    <div className="parent-space-between">
                        <p><Link className="backward-round" to='/'>Revenir</Link></p>
                    </div>

                    <h1 className="whiteSpecialTitle">Rencontre love</h1>

                    <div className="boxRounded dark-shadow">
                        {/* eslint-disable-next-line */}
                        <div
                            style={{
                                position: 'absolute',
                                top: 10,
                                left: 10,
                                border: '1px solid white',
                                borderRadius: '100%'
                            }}
                            onClick={this.setReturn.bind(this)}
                            role="button"
                            tabIndex="0"
                        >
                            <IconButton aria-label="delete" size="small" >
                                <ArrowBack fontSize="small" />
                            </IconButton>
                        </div>
                        <div
                            style={{
                                width: '100%',
                                height: 30
                            }}
                        >
                        </div>
                        <h2 className="whiteSecondTitle centeredText">
                            Parlez-moi de vous
                        </h2>
                        <p
                            style={{
                                fontSize: 19,
                                textAlign: 'left',
                                margin: '15px 0'
                            }}
                        >
                            Je suis: *
                        </p>
                        <div
                            className="inline"
                            style={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                flex: 1
                            }}
                        >
                            <Button
                                variant={ isHomme ? 'contained' : 'outlined'}
                                color={ isHomme ? 'primary' : 'secondary' }
                                style={{ borderRadius: 30, flex: 1, margin: 3 }}
                                onClick={this.onClickSexe.bind(this, 'Homme')}
                            >
                                Homme
                            </Button>
                            <Button
                                variant={ isFemme ? 'contained' : 'outlined'}
                                color={ isFemme ? 'primary' : 'secondary' }
                                style={{ borderRadius: 30, flex: 1, margin: 3 }}
                                onClick={this.onClickSexe.bind(this, 'Femme')}
                            >
                                Femme
                            </Button>
                        </div>
                        <div
                            className="margin-tb-20" >
                            <FormControl
                                required error={error}
                                component="fieldset"
                                style={{ float: 'left' }}
                            >
                                <FormLabel
                                    component="legend"
                                    style={{ textAlign: 'left' }}
                                >Je cherche</FormLabel>
                                <FormGroup
                                    style={{ height: 150 }}
                                >
                                    <FormControlLabel
                                        control={<Checkbox checked={Homme} onChange={this.onChangeCheck.bind(this)} name="Homme" />}
                                        label="Homme"
                                    />
                                    <FormControlLabel
                                        control={<Checkbox checked={Gays} onChange={this.onChangeCheck.bind(this)} name="Gays" />}
                                        label="Gays"
                                    />
                                    <FormControlLabel
                                        control={<Checkbox checked={Hommebi} onChange={this.onChangeCheck.bind(this)} name="Hommebi" />}
                                        label="Homme bi"
                                    />
                                    <FormControlLabel
                                        control={<Checkbox checked={Femme} onChange={this.onChangeCheck.bind(this)} name="Femme" />}
                                        label="Femme"
                                    />
                                    <FormControlLabel
                                        control={<Checkbox checked={Lesbiennes} onChange={this.onChangeCheck.bind(this)} name="Lesbiennes" />}
                                        label="Lesbiennes"
                                    />
                                    <FormControlLabel
                                        control={<Checkbox checked={Femmebi} onChange={this.onChangeCheck.bind(this)} name="Femmebi" />}
                                        label="Femme bi"
                                    />
                                </FormGroup>
                            </FormControl>
                            <ul className="standar-vertic-spacing">
                                <li>
                                    <input
                                        style={{ cursor: 'pointer' }}
                                        className="max-width btn button-light-blue"
                                        type="button"
                                        value={ isLoading ? 'Loading...' : 'Valider' }
                                        onClick={this.onSubmit.bind(this)}
                                    />
                                </li>
                            </ul>
                        </div>
                        <div>
                            <li className="centeredText small-padding "></li>
                            <li className="centeredText small-padding ">* Champ obligatoire</li>
                        </div>
                    </div>
                </ThemeProvider>
            </div>
        )
    }
}

export default Parlezvous
