import React from 'react'
import './statics/styles/Block.scss'
import Avatar from '@material-ui/core/Avatar'
import {
    MoreHoriz,
    Mail,
    ArrowDropDown,
    Unsubscribe,
    FilterNone,
    SpeakerNotesOff
} from '@material-ui/icons'
import Moment from 'react-moment'
import 'moment/locale/fr'
import { BeatLoader as Load } from 'react-spinners'

function Block ({ isLeft, url, text, typeKey }) {
    const [show, setShow] = React.useState(false)
    return (
        <div
            className='Block'
            style={{ flexDirection: isLeft ? 'row' : 'row-reverse' }}
            onMouseEnter={setShow(true)} 
            onMouseLeave={setShow(false)}
        >
            <div className='avatar'>
                <Avatar alt="Remy Sharp" src={ url } />
            </div>
            <div>
                <div
                    className={ 'body ' + (isLeft ? 'borderRaduisLeft' : 'borderRaduisRight')}
                >
                    { typeKey ? <Load/> : text }
                </div>

                <Moment
                    locale='fr'
                    fromNow
                    style={{ float: isLeft ? 'left' : 'right' }}
                >
                    { new Date() }
                </Moment>
            </div>
            <div
                style={{
                    // border: '1px solid red',
                    marginBottom: 15,
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    position: 'relative',
                    flexDirection: 'column'
                }}>
            { 
                show &&
                <div

                    style={{
                        // border: '1px solid green',
                        position: 'absolute',
                        transform: 'translateY(-40px)',
                        borderRadius: 10,
                        cursor: 'pointer'
                    }}
                >
                    <div
                        style={{ position: 'relative' }}
                    >
                        <div
                            style={{
                                position: 'relative',
                                display: 'flex',
                                backgroundColor: '#000000',
                                borderRadius: 10
                            }}
                        >
                            <Mail style={{ fontSize: 30, color: '#ffffff', margin: 10 }}/>
                            <FilterNone style={{ fontSize: 27, color: '#ffffff', margin: 10 }}/>
                            <SpeakerNotesOff style={{ fontSize: 30, color: '#ffffff', margin: 10 }}/>
                            <Unsubscribe style={{ fontSize: 33, color: '#ffffff', margin: 10 }}/>
                        </div>
                        <ArrowDropDown
                            style={{
                                position: 'absolute',
                                fontSize: 40,
                                left: '50%',
                                transform: 'translateX(-50%)',
                                bottom: -21,
                                zIndex: 10
                            }}
                        />
                    </div>
                </div>
                }
                { 
                    show &&
                    <MoreHoriz
                        style={{
                            fontSize: 33,
                            color: '#5b5b5b',
                            cursor: 'pointer'
                        }}
                    />
                }
            </div>
        </div>
    )
}

export default Block
