import React from 'react'
import './statics/styles/profdet.scss'
import Badge from '@material-ui/core/Badge'
import Avatar from '@material-ui/core/Avatar'
import { makeStyles, withStyles } from '@material-ui/core/styles'
import { ArrowRight, ArrowLeft } from '@material-ui/icons'

const StyledBadge = withStyles((theme) => ({
    badge: {
        backgroundColor: '#44b700',
        color: '#44b700',
        boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
        '&::after': {
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '50%',
            animation: '$ripple 1.2s infinite ease-in-out',
            border: '1px solid currentColor',
            content: '""'
        }
    },
    '@keyframes ripple': {
        '0%': {
            transform: 'scale(.8)',
            opacity: 1
        },
        '100%': {
            transform: 'scale(2.4)',
            opacity: 0
        }
    }
}))(Badge)

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1)
        }
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
        opacity: 0.5
    },
    normal: {
        opacity: 0.7
    },
    large: {
        width: theme.spacing(7),
        height: theme.spacing(7)
    }
}))

const datas = [
    'https://wl-sympa.cf.tsp.li/resize/728x/jpg/f6e/ef6/b5b68253409b796f61f6ecd1f1.jpg',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    'https://wl-sympa.cf.tsp.li/resize/728x/jpg/f6e/ef6/b5b68253409b796f61f6ecd1f1.jpg'
]

function BadgeAvatars ({ size, url, id }) {
    console.log(id, 'appeler', size)
    const classes = useStyles()
    // const stock = size !== 5 ? ((size < 2.5 || size > 7.5) ? classes.small : classes.normal) : classes.large
    let stock = 10
    if (size !== 5) {
        stock = Math.abs(5 - size)
    }
    stock = stock * 0.1
    // const stock = size !== 5 ? ((size < 2.5 || size > 7.5) ? classes.small : classes.normal) : classes.large
    return (
        <div>
            <StyledBadge
                overlap="circle"
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'right'
                }}
                variant="dot"
            >
                <Avatar
                // className={stock}
                    src={url}
                    style={{ width: 40, height: 40, transform: 'scale(' + stock + ')' }}
                    className='vitesseScale'
                    alt="user"
                />
            </StyledBadge>
        </div>
    )
}

function BoxActif (props) {
    const [users, setUsers] = React.useState([])
    const [first, setFirst] = React.useState(0)
    const [left, setLeft] = React.useState(0)

    const fontDataResize = (first, e, s) => {
        let newSize = s
        if (newSize % 2 === 0) {
            newSize = newSize - 1
        }
        let size = newSize
        if (e.length < newSize) {
            size = e.length
        }
        let resp = []
        const pas = 1 / newSize
        let z = 0
        for (let i = 0; i < e.length; i++) {
            if (i < first) {
                resp = [...resp, [i, e[i], 0]]
            } else
            if (i > size) {
                resp = [...resp, [i, e[i], 0]]
            } else {
                z = z + pas
                resp = [...resp, [i, e[i], parseInt(z * 10)]]
                // resp = [...resp, [e[i], z]]
            }
        }
        return resp
    }

    const resizeUser = () => {

    }

    React.useEffect(() => {
        const stock = fontDataResize(first, datas, 7)
        console.log(stock)
        setUsers(stock)
    }, [first])

    return (
        <div style={{ display: 'flex', alignItems: 'center' }}>
            <ArrowLeft
                style={{
                    color: '#e4e3df',
                    fontSize: 50,
                    cursor: 'pointer'
                }}
                onClick={() => {
                    console.log('click moin')
                    setFirst(e => e - 1)
                    setLeft(e => e + 40)
                }}
            />
            <div
                style={{
                    position: 'relative',
                    border: '1px solid red',
                    width: 345,
                    overflow: 'hidden',
                    height: 72
                }}
            >
                <div
                    style={{
                        position: 'absolute',
                        display: 'flex',
                        alignItems: 'center',
                        backgroundColor: 'green',
                        transform: 'translateX(' + left + 'px)',
                        height: 72
                    }}
                    className='vitesseTranslate'
                >
                    {
                        users.map(([id, e, size]) => {
                            return (<div key={id} style={{ padding: 10 }}>
                                <BadgeAvatars
                                    size={size}
                                    url={e}
                                    id={id}
                                />
                            </div>)
                        })
                    }
                </div>
            </div>
            <ArrowRight
                style={{
                    color: '#e4e3df',
                    fontSize: 50,
                    cursor: 'pointer'
                }}
                onClick={() => {
                    console.log('click plus')
                    setFirst(e => e + 1)
                    setLeft(e => e - 40)
                }}
            />
        </div>
    )
}

export default BoxActif
