import React from 'react'
import './statics/styles/profdet.scss'
import Badge from '@material-ui/core/Badge'
import Avatar from '@material-ui/core/Avatar'
import { makeStyles, withStyles } from '@material-ui/core/styles'
import { ArrowRight, ArrowLeft } from '@material-ui/icons'

const StyledBadge = withStyles((theme) => ({
    badge: {
        backgroundColor: '#44b700',
        color: '#44b700',
        boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
        '&::after': {
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '50%',
            animation: '$ripple 1.2s infinite ease-in-out',
            border: '1px solid currentColor',
            content: '""'
        }
    },
    '@keyframes ripple': {
        '0%': {
            transform: 'scale(.8)',
            opacity: 1
        },
        '100%': {
            transform: 'scale(2.4)',
            opacity: 0
        }
    }
}))(Badge)

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1)
        }
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
        opacity: 0.5
    },
    normal: {
        opacity: 0.7
    },
    large: {
        width: theme.spacing(7),
        height: theme.spacing(7)
    }
}))

function BadgeAvatars ({ size, url }) {
    const classes = useStyles()
    return (
        <div className={classes.root}>
            <StyledBadge
                overlap="circle"
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'right'
                }}
                variant="dot"
            >
                <Avatar
                    className={size}
                    src={url}
                    alt="user"
                />
            </StyledBadge>
        </div>
    )
}

const datas = [
    'https://wl-sympa.cf.tsp.li/resize/728x/jpg/f6e/ef6/b5b68253409b796f61f6ecd1f1.jpg',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    '',
    ''
]

function BoxActif (props) {
    const [users, setUsers] = React.useState([])
    const [first, setFirst] = React.useState(0)
    const fontDataResize = (first, e, s, min, max) => {
        let newSize = s
        if (newSize % 2 === 0) {
            newSize = newSize - 1
        }
        let size = newSize
        if (e.length < newSize) {
            size = e.length
        }
        const resp = []
        const pas = 1 / newSize
        let z = 0
        for (let i = 0; i <= size; i++) {
            z = z + pas
            resp.push([e[i], parseInt(z * 10)])
        }
        return resp
    }

    React.useEffect(() => {
        setUsers(fontDataResize(first, datas, 7))
    }, [first])

    const classes = useStyles()
    return (
        <div style={{ display: 'flex', alignItems: 'center' }}>
            <ArrowLeft
                style={{
                    color: '#e4e3df',
                    fontSize: 50,
                    cursor: 'pointer'
                }}
                onClick={() => {
                    console.log('click moin')
                    setFirst(e => e - 1)
                }}
            />
            {
                users.map(([e, size], key) => {
                    const stock = size !== 5 ? ((size < 2.5 || size > 7.5) ? classes.small : classes.normal) : classes.large
                    return (<div key={key}>
                        <BadgeAvatars
                            size={stock}
                            url={e}
                        />
                    </div>)
                })
            }
            <ArrowRight
                style={{
                    color: '#e4e3df',
                    fontSize: 50,
                    cursor: 'pointer'
                }}
                onClick={() => setFirst(e => e + 1)}
            />
        </div>
    )
}

export default BoxActif
