import React from 'react'
import Badge from '@material-ui/core/Badge'
import Avatar from '@material-ui/core/Avatar'
import { makeStyles, withStyles } from '@material-ui/core/styles'
import './statics/styles/Header.scss'

import {
    ArrowBack,
    MoreVert
} from '@material-ui/icons'

const StyledBadge = withStyles((theme) => ({
    badge: {
        backgroundColor: '#44b700',
        color: '#44b700',
        boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
        '&::after': {
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '50%',
            animation: '$ripple 1.2s infinite ease-in-out',
            border: '1px solid currentColor',
            content: '""'
        }
    },
    '@keyframes ripple': {
        '0%': {
            transform: 'scale(.8)',
            opacity: 1
        },
        '100%': {
            transform: 'scale(2.4)',
            opacity: 0
        }
    }
}))(Badge)

const SmallAvatar = withStyles((theme) => ({
    root: {
        width: 22,
        height: 22,
        border: `2px solid ${theme.palette.background.paper}`
    }
}))(Avatar)

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1)
        }
    }
}))

function BadgeAvatars () {
    const classes = useStyles()

    return (
        <div className={classes.root}>
            <Badge
                overlap="circle"
                anchorOrigin={{
                    vertical: 'top',
                    horizontal: 'right'
                }}
                badgeContent={<div className='badge'>2</div>}
            >
                <Avatar alt="Travis Howard" src="https://images-na.ssl-images-amazon.com/images/I/81lKUxkmF5L._SY600_.jpg" />
            </Badge>
        </div>
    )
}

function Header () {
    return (
        <div className='Header'>
            <div>
                <ArrowBack />
            </div>
            <div>
                <BadgeAvatars/>
            </div>
            <div>
                <BadgeAvatars/>
            </div>
            <div>
                <BadgeAvatars/>
            </div>
            <div className='veronique'>
                <h1>Veronique</h1>
                <BadgeAvatars/>
            </div>
            <div>
                <BadgeAvatars/>
            </div>
            <div>
                <BadgeAvatars/>
            </div>
            <div>
                <BadgeAvatars/>
            </div>
            <div>
                <MoreVert/>
            </div>
        </div>
    )
}

export default Header
