import React, { Component } from 'react'
import Block from './layouts/block/Block'
import Header from './layouts/header/Header'
import './statics/styles/Chat.scss'
import Moment from 'react-moment'
import ChatInput from './layouts/chatInput/ChatInput'
import Notification from './layouts/notifications/Notification'
import axios from 'axios'
import socketIOClient from 'socket.io-client'

function HeaderType ({ title, date }) {
    return (
        <div className='HeaderType'>
            <div className='lineH'></div>
            <div className='center'>
                <h1>{ title }</h1>
                <Moment format="dddd DD MMMM YYYY">
                    { date }
                </Moment>
            </div>
            <div className='lineH'></div>
        </div>
    )
}

class Chat extends Component {
    constructor (props) {
        super(props)
        this.state = {
            showNotification: false,
            datas: [],
            userContacter: null,
            listUser: [],
            userActif: {}
        }
        this.urlconv = '/api/get_messages'
        this.socket = socketIOClient('https://evening-basin-72730.herokuapp.com/')
    }

    setListen () {
        const { userContacter, userActif } = this.state
        console.log(userContacter, userActif)
        if (userActif.Information && userContacter.user_id) {
            const eventName = 'SendMessage_from' + userActif.Information.user_id + 'to' + userContacter.user_id
            try {
                this.socket.on(eventName, ({ data }) => {
                    this.setState((prevState) => {
                        return { datas: data }
                    })
                })
            } catch (e) {
                console.log(e)
            }
        }
    }

    toogleShowNotification () {
        this.setState({ showNotification: !this.state.showNotification })
    }

    getUserConnect () {
        const Authorization = 'Bearer ' + JSON.parse(localStorage.getItem('Token'))
        axios
            .get('/api/profil', {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: Authorization
                }
            })
            .then(({ data }) => {
                this.setState({ userActif: data })
            })
            .catch(error => console.log({ error }))
    }

    async componentDidMount () {
        // this.setState({ datas: sms, listUser })
        this.setListen()
        await this.getUserConnect()
    }

    getConvertion (userContacter) {
        const { userActif } = this.state
        this.setState({ userContacter })
        if (userActif.Information && userContacter.user_id) {
            if (localStorage.hasOwnProperty('Token')) {
                axios.post(this.urlconv, {
                    user_1: userActif.Information.user_id,
                    user_2: userContacter.user_id
                }, {
                    headers: {
                        Authorization: 'Bearer ' + JSON.parse(localStorage.getItem('Token'))
                    }
                })
                    .then(({ data }) => {
                        this.setState({ datas: data })
                    })
                    .catch(error => console.log({ error }))
            } else {
                console.log('Token not found')
            }
        }
    }

    sortData (datas, key) {
        return Object.entries(datas).map(([key, values]) => values).sort((a, b) => {
            return b[key] - a[key]
        })
    }

    onSubmit (value, jointe) {
        const { userContacter, userActif } = this.state
        const payload = {
            user_1: userActif.Information.user_id,
            user_2: userContacter.user_id,
            message: value,
            token: JSON.parse(localStorage.getItem('Token'))
        }
        this.socket.emit('recevePayload', payload)
    }

    findOne (data, key, search) {
        return data.filter(e => {
            return e[key] === search
        })
    }

    render () {
        const Rencontre = () => <HeaderType title='LA RENCONTRE' date={new Date()}/>
        const Decouvert = () => <HeaderType title='LA DECOUVERTE' date={new Date()}/>
        const RendezVous = () => <HeaderType title='LE RENDEZ-VOUS' date={new Date()}/>
        const {
            showNotification,
            datas,
            userActif,
            userContacter
        } = this.state
        const dats = this.sortData(datas, 'id')

        let init = 0
        let us = 0
        let one = false

        const lenDecouvert = 5
        const lenRendezVous = 6
        return (
            <div className='Chat'>
                {
                    showNotification && <Notification toogleShowNotification={this.toogleShowNotification.bind(this)}/>
                }
                <div className='container'>
                    <Header userContacter={this.getConvertion.bind(this)} userActif={userActif} />
                    {
                        dats && dats.map((e, i) => {
                            one = false
                            if (us !== e.utilisateurs_1_id) {
                                init = init + 1
                                us = e.utilisateurs_1_id
                                if ((init === lenDecouvert || init === lenRendezVous) && dats.length !== i + 1) {
                                    one = true
                                }
                            }
                            const urlImage = e.utilisateurs_1_id === userActif.Information.user_id ? userContacter.url : userActif.User.profil_image
                            return <div key={i}>
                                { i === 0 && <Rencontre/> }
                                <Block
                                    typeKey={false}
                                    isLeft={e.utilisateurs_1_id === userActif.Information.user_id}
                                    url={urlImage}
                                    text={e.messages_contenue}
                                    date={e.created_at}
                                />
                                { init === lenDecouvert && one && <Decouvert/> }
                                { init === lenRendezVous && one && <RendezVous/> }
                            </div>
                        })
                    }
                    <ChatInput onSubmit={this.onSubmit.bind(this)}/>
                </div>
            </div>
        )
    }
}

export default Chat
