import React, { useState } from 'react'
import TextField from '@material-ui/core/TextField'
import { useDispatch, useSelector } from 'react-redux'
import Button from '@material-ui/core/Button'
import ButtonGroup from '@material-ui/core/ButtonGroup'
import { makeStyles } from '@material-ui/core/styles'
import UserIcon from '@material-ui/icons/Save'

import InputLabel from '@material-ui/core/InputLabel'
import MenuItem from '@material-ui/core/MenuItem'
import FormControl from '@material-ui/core/FormControl'
import Select from '@material-ui/core/Select'

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        '& > *': {
            margin: theme.spacing(1),
            width: '100%'
        }
    }
}))

function Particulier () {
    const dispatch = useDispatch()
    const state = useSelector(state => state.Contacts)

    const onChangeInput = (e) => {
        dispatch({
            type: 'ON_CHANGE_CONTACTS',
            datas: { [e.target.name]: e.target.value }
        })
    }
    return (
        <>
        <div
            style={{
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%'
            }}>
            <TextField
                label="Prénom*"
                value={state.Prenom}
                onChange={onChangeInput}
                variant="outlined"
                margin="dense"
                name="Prenom"
            />
            <div style={{ width: 10 }}></div>
            <TextField
                label="Nom*"
                value={state.Nom}
                onChange={onChangeInput}
                margin="dense"
                variant="outlined"
                name="Nom"
            />
        </div>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    width: '100%'
                }}>
                <TextField
                    label="Email*"
                    value={state.Email}
                    onChange={onChangeInput}
                    margin="dense"
                    variant="outlined"
                    name="Email"
                />
                <div style={{ width: 10 }}></div>
                <TextField
                    label="Téléphone*"
                    value={state.Tel}
                    onChange={onChangeInput}
                    margin="dense"
                    variant="outlined"
                    name="Tel"
                />
            </div>
        </>
    )
}

function Professionnel () {
    const dispatch = useDispatch()
    const state = useSelector(state => state.Contacts)

    const onChangeInput = (e) => {
        dispatch({
            type: 'ON_CHANGE_CONTACTS',
            datas: { [e.target.name]: e.target.value }
        })
    }
    return (
        <>
        <div
            style={{
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%'
            }}>
            <TextField
                label="Entreprise*"
                value={state.Entreprise}
                onChange={onChangeInput}
                margin="dense"
                variant="outlined"
                name="Entreprise"
            />
        </div>
        <div
            style={{
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%'
            }}>
            <TextField
                label="Ville*"
                value={state.Ville}
                onChange={onChangeInput}
                variant="outlined"
                margin="dense"
                name="Ville"
            />
            <div style={{ width: 10 }}></div>
            <TextField
                label="Code Postal*"
                value={state.CodePostal}
                onChange={onChangeInput}
                margin="dense"
                variant="outlined"
                name="CodePostal"
            />
        </div>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    width: '100%'
                }}>
                <TextField
                    label="Adresse*"
                    value={state.Adresse}
                    onChange={onChangeInput}
                    margin="dense"
                    variant="outlined"
                    name="Adresse"
                />
                <div style={{ width: 10 }}></div>
                <FormControl
                    variant="outlined"
                    className={classes.formControl}
                >
                    <InputLabel id="demo-simple-select-outlined-label">Age</InputLabel>
                    <Select
                        labelId="demo-simple-select-outlined-label"
                        id="demo-simple-select-outlined"
                        value={state.Pays}
                        onChange={onChangeInput}
                        label="Age"
                    >
                        <MenuItem value={10}>Ten</MenuItem>
                        <MenuItem value={20}>Twenty</MenuItem>
                        <MenuItem value={30}>Thirty</MenuItem>
                    </Select>
                </FormControl>
            </div>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    width: '100%'
                }}>
                <TextField
                    label="Message"
                    value={state.Message}
                    onChange={onChangeInput}
                    multiline
                    rows={5}
                    margin="dense"
                    variant="outlined"
                    name="Message"
                />
            </div>
        </>
    )
}

function Positions () {
    const classes = useStyles()
    const [pages, setPages] = useState('Particulier')
    return (
        <>
            <h1>Parler à un conseiller: 09 72 65 24 84</h1>
            <div className={classes.root}>
                <ButtonGroup
                    variant="contained"
                    color="primary"
                    aria-label="contained primary button group"
                >
                    <Button
                        variant={
                            pages === 'Particulier'
                                ? 'contained'
                                : 'outlined'
                        }
                        color="primary"
                        size="large"
                        onClick={() => setPages('Particulier')}
                        className={classes.button}
                        startIcon={<UserIcon />}
                        style={{ flex: 1 }}
                    >Particulier</Button>
                    <Button
                        variant={
                            pages === 'Professionnel'
                                ? 'contained'
                                : 'outlined'
                        }
                        color="primary"
                        size="large"
                        onClick={() => setPages('Professionnel')}
                        className={classes.button}
                        startIcon={<UserIcon />}
                        style={{ flex: 1 }}
                    >Professionnel</Button>
                </ButtonGroup>
            </div>
            {
                pages === 'Particulier' && <Particulier/>
            }
            {
                pages === 'Professionnel' && <Professionnel/>
            }
        </>
    )
}

export default Positions
