import React from 'react'
import Badge from '@material-ui/core/Badge'
import Avatar from '@material-ui/core/Avatar'
import { makeStyles } from '@material-ui/core/styles'
import './statics/styles/Header.scss'
import { Link } from 'react-router-dom'

import {
    ArrowBack,
    MoreVert
} from '@material-ui/icons'

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1)
        }
        // transition: 'transform 1000ms cubic-bezier(0,.98,.3,.95)'
    }
}))

function BadgeAvatars ({ user, notification, url }) {
    const classes = useStyles()
    let scale = 1
    let trans = 5
    if (user === 3) {
        scale = 1.7
    }
    if (user === 2 || user === 4) {
        trans = 42
    }
    if (user === 1 || user === 5) {
        trans = 37
    }
    if (user === 0 || user === 6) {
        trans = 27
    }

    return (
        <div
            className={classes.root}
            style={{ transform: 'scale(' + scale + ') translateY(' + trans + 'px)' }}
        >
            <Badge
                overlap="circle"
                anchorOrigin={{
                    vertical: 'top',
                    horizontal: 'right'
                }}
                badgeContent={notification !== 0 && <div className='badge'>{notification}</div>}
            >
                <Avatar
                    // style={{ width: user ? widthUser : 40, height: user ? widthUser : 40 }}
                    style={{ width: 40, height: 40 }}
                    alt="Travis Howard"
                    src={url}
                />
            </Badge>
        </div>
    )
}

// const insert = (arr, index, newItem) => [
//     ...arr.slice(0, index),
//     newItem,
//     ...arr.slice(index)
// ]
const move = (from, to, ...a) => from === to ? a : (a.splice(to, 0, ...a.splice(from, 1)), a)

function Header ({ userContacter, userActif }) {
    const center = 3
    const [datas, setDatas] = React.useState([])
    const [datasList, setDataList] = React.setState([])

    React.useEffect(() => {
        const datas = [
            { user_id: 1, notification: 0, url: 'https://www.village-justice.com/articles/local/cache-gd2/61/b91c0bc706676d96e362711536df5b.jpg?1603207633' },
            { user_id: 2, notification: 0, url: 'https://wl-sympa.cf.tsp.li/resize/728x/jpg/f6e/ef6/b5b68253409b796f61f6ecd1f1.jpg' },
            { user_id: 3, notification: 2, url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcS9e3tHfGvC1Z9xPpa_bU8ynCuWg_rn-C1YBQ&usqp=CAU' },
            { user_id: 4, notification: 0, url: 'https://images-na.ssl-images-amazon.com/images/I/81lKUxkmF5L._SY600_.jpg' },
            { user_id: 5, notification: 0, url: 'https://www.fourchette-et-bikini.fr/sites/default/files/styles/full_670x350/public/femme_amoureuse.jpg?itok=ovXsCBSe' },
            { user_id: 6, notification: 0, url: 'https://www.village-justice.com/articles/local/cache-gd2/61/b91c0bc706676d96e362711536df5b.jpg?1603207633' },
            { user_id: 7, notification: 0, url: 'https://wl-sympa.cf.tsp.li/resize/728x/jpg/f6e/ef6/b5b68253409b796f61f6ecd1f1.jpg' },
            { user_id: 8, notification: 2, url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcS9e3tHfGvC1Z9xPpa_bU8ynCuWg_rn-C1YBQ&usqp=CAU' },
            { user_id: 9, notification: 0, url: 'https://images-na.ssl-images-amazon.com/images/I/81lKUxkmF5L._SY600_.jpg' },
            { user_id: 10, notification: 0, url: 'https://www.fourchette-et-bikini.fr/sites/default/files/styles/full_670x350/public/femme_amoureuse.jpg?itok=ovXsCBSe' }
        ]
        datas && setDataList(datas)
    }, [])

    // eslint-disable-next-line
    const delay = ms => new Promise(res => setTimeout(res, ms))

    const dimIndex = async (e) => {
        for (let i = 0; i < e; i++) {
            // setCenter(a => a + 1)
            // move the first to last
            setDatas(datas => move(0, datas.length - 1, ...datas))
            await delay(500)
        }
    }

    const augIndex = async (e) => {
        for (let i = 0; i < e; i++) {
            // setCenter(a => a - 1)
            // move the last to first
            setDatas(datas => move(datas.length - 1, 0, ...datas))
            await delay(500)
        }
    }

    React.useEffect(() => {
        if (userContacter && datas.length > 0) {
            const stock = datas[center]
            if (stock) {
                userContacter(stock.id)
            }
        }
    }, [datas, userActif])

    return (
        <div className='Header'>
            <div>
                <Link to='/Profiles'>
                    <ArrowBack style={{ color: 'white' }} />
                </Link>
            </div>

            <h1>Veronique</h1>
            <div className='bodyBody'>
                <div
                    style={{
                        width: 74 * datas.length
                        // transform: 'translateX(' + (-74 * (center - 3)) + 'px)',
                    }}
                    className='body'
                >
                    {
                        datas && datas.map(({ notification, url, id }, i) => {
                        /* eslint-disable */
                        if (i < center) {
                            return (<div key={id} className='bl' onClick={() => augIndex(center - i)} >
                                <BadgeAvatars user={i} url={url} notification={id} />
                            </div>)
                        }
                        if (i === center) {
                            return (<div key={id} className='bl veronique' >
                                <BadgeAvatars user={i} url={url} notification={id} />
                            </div>)
                        }
                        if (i > center) {
                            return (
                                <div key={id} className='bl' onClick={() => dimIndex(i - center)} >
                                    <BadgeAvatars user={i} url={url} notification={id} />
                                </div>
                            )
                        }
                        /* eslint-enable */
                        })
                    }
                </div>
            </div>
            <div>
                <MoreVert style={{ color: 'white' }} />
            </div>
        </div>
    )
}

export default Header
