import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import {
    IconButton,
    Button
} from '@material-ui/core'
import {
    createMuiTheme,
    ThemeProvider
} from '@material-ui/core/styles'

import {
    LocationOn,
    ArrowBack
} from '@material-ui/icons'

const theme = createMuiTheme({
    palette: {
        type: 'dark',
        secondary: {
            main: '#d3d3d3'
        }
    },
    typography: {
        button: {
            textTransform: 'none'
        }
    }
})

const diametre = 300
const padding = 30
class Geolocalisation extends Component {
    constructor (props) {
        super(props)
        this.state = {
            top: ((diametre - (3 * padding)) / 2),
            left: ((diametre - (3 * padding)) / 2)
        }
    }

    setReturn () {
        if (this.state.first) {
            this.props.history.push('/Authentification=Inscription')
        } else {
            this.setState({ first: true })
        }
    }

    render () {
        const {
            left,
            top
        } = this.state
        return (
            <div className='Connection'>
                <ThemeProvider theme={theme}>
                    <div className="parent-space-between">
                        <p><Link className="backward-round" to='/'>Revenir</Link></p>
                    </div>

                    <h1 className="whiteSpecialTitle">Rencontre love</h1>

                    <div className="boxRounded dark-shadow">
                        {/* eslint-disable-next-line */}
                        <div
                            style={{
                                position: 'absolute',
                                top: 10,
                                left: 10,
                                border: '1px solid white',
                                borderRadius: '100%'
                            }}
                            onClick={this.setReturn.bind(this)}
                            role="button"
                            tabIndex="0"
                        >
                            <IconButton aria-label="delete" size="small" >
                                <ArrowBack fontSize="small" />
                            </IconButton>
                        </div>
                        <div
                            style={{
                                width: '100%',
                                height: 30
                            }}
                        >
                        </div>
                        <h2 className="whiteSecondTitle centeredText">
                          Ma Position GPS
                        </h2>
                        <div>
                            <p>
                                Perle rencontre essaie de géolocalisation votre position
                            </p>
                            <div
                                style={{ height: diametre, width: diametre }}>
                                <div
                                    style={{
                                        height: diametre - padding,
                                        width: diametre - padding
                                    }}
                                    className='circleLocation'
                                >
                                    <div
                                        style={{
                                            height: diametre - (2 * padding),
                                            width: diametre - (2 * padding)
                                        }}
                                        className='circleLocation'
                                    >
                                        <div
                                            style={{
                                                position: 'relative',
                                                height: diametre - (3 * padding),
                                                width: diametre - (3 * padding)
                                            }}
                                            className='circleLocation'
                                        >
                                            <div>
                                                <LocationOn
                                                    style={{
                                                        fontSize: 100,
                                                        position: 'absolute',
                                                        left: left,
                                                        top: top
                                                    }}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <Button
                                variant="contained"
                                color="secondary"
                                size="small"
                                style={{ borderRadius: 30 }}
                            >
                                Google
                            </Button>
                        </div>
                    </div>
                </ThemeProvider>
            </div>
        )
    }
}

export default Geolocalisation
