import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import {
    IconButton,
    Button
} from '@material-ui/core'
import {
    createMuiTheme,
    ThemeProvider
} from '@material-ui/core/styles'

import {
    LocationOn,
    ArrowBack
} from '@material-ui/icons'

const theme = createMuiTheme({
    palette: {
        type: 'dark',
        secondary: {
            main: '#d3d3d3'
        }
    },
    typography: {
        button: {
            textTransform: 'none'
        }
    }
})

const diametre = 300
const padding = 30
class Geolocalisation extends Component {
    state = {
        isMove: true,
        latitude: '',
        longitude: ''
    }

    setReturn () {
        this.props.history.push('/Authentification=Inscription')
    }

    async geoloc () {
        console.log('geoloc ...')
        await navigator.geolocation.getCurrentPosition(
            position => this.setState({
                latitude: position.coords.latitude,
                longitude: position.coords.longitude
            }),
            err => console.log(err)
            ,{ enableHighAccuracy: true }
        )
    }

    componentDidMount () {
        this.geoloc()
        setTimeout(() => {
            console.log(this.state)
            this.setState({ isMove: false })
        }, 10000)
    }

    render () {
        const {
            isMove
        } = this.state
        return (
            <div className='Connection'>
                <ThemeProvider theme={theme}>
                    <div className="parent-space-between">
                        <p><Link className="backward-round" to='/'>Revenir</Link></p>
                    </div>

                    <h1 className="whiteSpecialTitle">Rencontre love</h1>

                    <div className="boxRounded dark-shadow">
                        {/* eslint-disable-next-line */}
                        <div
                            style={{
                                position: 'absolute',
                                top: 10,
                                left: 10,
                                border: '1px solid white',
                                borderRadius: '100%'
                            }}
                            onClick={this.setReturn.bind(this)}
                            role="button"
                            tabIndex="0"
                        >
                            <IconButton aria-label="delete" size="small" >
                                <ArrowBack fontSize="small" />
                            </IconButton>
                        </div>
                        <div
                            style={{
                                width: '100%',
                                height: 30
                            }}
                        >
                        </div>
                        <h2 className="whiteSecondTitle centeredText">
                          Ma Position GPS
                        </h2>
                        <div>
                            <div
                                style={{ height: diametre, width: diametre }}>
                                <div
                                    style={{
                                        height: diametre - padding,
                                        width: diametre - padding
                                    }}
                                    className='circleLocation'
                                >
                                    <div
                                        style={{
                                            height: diametre - (2 * padding),
                                            width: diametre - (2 * padding)
                                        }}
                                        className='circleLocation'
                                    >
                                        <div
                                            style={{
                                                position: 'relative',
                                                height: diametre - (3 * padding),
                                                width: diametre - (3 * padding),
                                                display: 'block'
                                            }}
                                            className='circleLocation'
                                        >
                                            <div>
                                                <LocationOn
                                                    className={isMove ? 'cursorLocatisation' : ''}
                                                    style={{
                                                        fontSize: 100,
                                                        position: 'absolute'
                                                    }}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div
                                style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    margin: '20px 0'
                                }}
                            >
                                <p style={{ fontSize: 20, overflowWrap: 'break-word', width: 250, textAlign: 'center' }}>
                                    Perle rencontre essaie de g√©olocalisation votre position
                                </p>
                            </div>

                            <Button
                                variant="contained"
                                color="secondary"
                                size="small"
                                style={{
                                    borderRadius: 30,
                                    backgroundColor: '#eb565a',
                                    color: 'white'
                                }}
                            >
                                je ne souhaite pas etre localiser
                            </Button>
                        </div>
                    </div>
                </ThemeProvider>
            </div>
        )
    }
}

export default Geolocalisation
