import React from 'react'
import './statics/styles/profdet.scss'
import Badge from '@material-ui/core/Badge'
import Avatar from '@material-ui/core/Avatar'
import { makeStyles, withStyles } from '@material-ui/core/styles'
import { ArrowRight, ArrowLeft } from '@material-ui/icons'

const StyledBadge = withStyles((theme) => ({
    badge: {
        backgroundColor: '#44b700',
        color: '#44b700',
        boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
        '&::after': {
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '50%',
            animation: '$ripple 1.2s infinite ease-in-out',
            border: '1px solid currentColor',
            content: '""'
        }
    },
    '@keyframes ripple': {
        '0%': {
            transform: 'scale(.8)',
            opacity: 1
        },
        '100%': {
            transform: 'scale(2.4)',
            opacity: 0
        }
    }
}))(Badge)

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1)
        }
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
        opacity: 0.5
    },
    normal: {
        opacity: 0.7
    },
    large: {
        width: theme.spacing(7),
        height: theme.spacing(7)
    }
}))

const datas = [
    { id: 0, url: 'https://wl-sympa.cf.tsp.li/resize/728x/jpg/f6e/ef6/b5b68253409b796f61f6ecd1f1.jpg' },
    { id: 1, url: '' },
    { id: 2, url: '' },
    { id: 3, url: '' },
    { id: 4, url: '' },
    { id: 5, url: '' },
    { id: 6, url: '' },
    { id: 7, url: '' },
    { id: 8, url: '' },
    { id: 9, url: '' },
    { id: 10, url: '' },
    { id: 11, url: '' },
    { id: 12, url: '' },
    { id: 13, url: '' },
    { id: 14, url: '' },
    { id: 15, url: '' },
    { id: 16, url: '' },
    { id: 17, url: '' },
    { id: 18, url: '' },
    { id: 19, url: '' },
    { id: 20, url: '' },
    { id: 21, url: 'https://wl-sympa.cf.tsp.li/resize/728x/jpg/f6e/ef6/b5b68253409b796f61f6ecd1f1.jpg' }
]

function BadgeAvatars ({ size, url, id }) {
    console.log(id, 'appeler', size)
    const classes = useStyles()
    // const stock = size !== 5 ? ((size < 2.5 || size > 7.5) ? classes.small : classes.normal) : classes.large
    // let stock = 10
    // if (size !== 5) {
    //     stock = Math.abs(5 - size)
    // }
    // stock = stock * 0.1
    return (
        <div style={{ transform: 'scale(' + size + ')' }} >
            <StyledBadge
                overlap="circle"
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'right'
                }}
                variant="dot"
            >
                <Avatar
                // className={stock}
                    src={url}
                    style={{ width: 40, height: 40, transform: 'scale(' + size + ')' }}
                    className='vitesseScale'
                    alt="user"
                />
            </StyledBadge>
        </div>
    )
}

function BoxActif (props) {
    // const [users, setUsers] = React.useState([])
    const [first, setFirst] = React.useState(0)
    const [left, setLeft] = React.useState(0)
    const [btleft, setBtnLeft] = React.useState(false)
    const [btright, setBtnRight] = React.useState(0)

    React.useEffect(() => {
        setBtnLeft(true)
        setBtnRight(true)
        if (first === 0) {
            setBtnLeft(false)
        }
        if ((first + 7) === datas.length) {
            setBtnRight(false)
        }
    }, [first])

    return (
        <div style={{ display: 'flex', alignItems: 'center' }}>
            <ArrowLeft
                style={{
                    color: '#e4e3df',
                    fontSize: 50,
                    cursor: 'pointer'
                }}
                onClick={() => {
                    if (btleft) {
                        setFirst(e => e - 1)
                        setLeft(e => e + 62)
                    }
                }}
            />
            <div
                style={{
                    position: 'relative',
                    border: '1px solid red',
                    width: 434,
                    overflow: 'hidden',
                    height: 72
                }}
            >
                <div
                    style={{
                        position: 'absolute',
                        display: 'flex',
                        alignItems: 'center',
                        backgroundColor: 'green',
                        transform: 'translateX(' + left + 'px)',
                        height: 72
                    }}
                    className='vitesseTranslate'
                >
                    {
                        datas.map(({ id, url }) => {
                            let size = 0.65
                            if ((first + 1) === id || (first + 5) === id) {
                                size = 0.7
                            }

                            if ((first + 2) === id || (first + 4) === id) {
                                size = 0.85
                            }

                            if ((first + 3) === id) {
                                size = 1
                            }

                            return (<div key={id} style={{ width: 40, border: '1px solid red', padding: 10 }}>
                                <BadgeAvatars
                                    size={size}
                                    url={url}
                                    id={id}
                                />
                            </div>)
                        })
                    }
                </div>
            </div>
            <ArrowRight
                style={{
                    color: '#e4e3df',
                    fontSize: 50,
                    cursor: 'pointer'
                }}
                onClick={() => {
                    if (btright) {
                        setFirst(e => e + 1)
                        setLeft(e => e - 62)
                    }
                }}
            />
        </div>
    )
}

export default BoxActif
