import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import {
    IconButton,
    Button
} from '@material-ui/core'
import {
    createMuiTheme,
    ThemeProvider
} from '@material-ui/core/styles'

import {
    LocationOn,
    ArrowBack
} from '@material-ui/icons'

const theme = createMuiTheme({
    palette: {
        type: 'dark',
        secondary: {
            main: '#d3d3d3'
        }
    },
    typography: {
        button: {
            textTransform: 'none'
        }
    }
})

class Geolocalisation extends Component {
    constructor (props) {
        super(props)
        this.state = {
            error: false,
            isLoading: false,
            Homme: false,
            Femme: false,
            Gays: false,
            Lesbiennes: false,
            Hommebi: false,
            Femmebi: false,
            location: '',
            first: true
        }
    }

    onSubmit (e) {
        e.preventDefault()
        const {
            email,
            password,
            first
        } = this.state
        if (first) {
            if (email !== '' && password !== '') {
                this.setState({ isLoading: true })
                axios
                    .post('/api/login', {
                        email,
                        password
                    })
                    .then(async ({ data: { token } }) => {
                        this.setState({ isLoading: false })
                        this.setState({ first: false })
                    })
                    .catch(e => {
                        this.setState({ isLoading: false, error: true })
                    })
                //
                console.log(email)
            }
            console.log(first)
        } else {
            if (email !== '' && password !== '') {
                this.setState({ isLoading: true })
                axios
                    .post('/api/login', {
                        email,
                        password
                    })
                    .then(async ({ data: { token } }) => {
                        this.setState({ isLoading: false })
                        await localStorage.setItem('Token', JSON.stringify(token))
                        this.props.history.push('/Profiles')
                    })
                    .catch(e => {
                        this.setState({ isLoading: false, error: true })
                    })
            }
            console.log(first + 'auther log ')
        }
    }

    onChangeInput (e) {
        this.setState({ [e.target.name]: e.target.value, error: false })
    }

    onChangeCheck (e) {
        this.setState({ [e.target.name]: e.target.checked, error: false })
        console.log(e.target.name)
    }

    initData (val) {
        const tab = []
        if (val === 'dd') {
            for (let i = 1; i < 32; i++) {
                tab.push(i)
            }
        } else if (val === 'mm') {
            const mois = ['janvier', 'février', 'mars', 'avril', 'mai', 'juin', 'juillet', 'août', 'septembre', 'octobre', 'novembre', 'décembre']
            for (let i = 0; i < mois.length; i++) {
                tab.push(mois[i])
            }
        } else {
            const years = new Date().getFullYear()
            for (let i = 0; i < 100; i++) {
                tab.push(years - i)
            }
        }
        return tab
    }

    setReturn () {
        if (this.state.first) {
            this.props.history.push('/Authentification=Inscription')
        } else {
            this.setState({ first: true })
        }
    }

    render () {
        const diametre = 300
        const padding = 30
        const {
            error,
            isLoading
        } = this.state
        return (
            <div className='Connection'>
                <ThemeProvider theme={theme}>
                    <div className="parent-space-between">
                        <p><Link className="backward-round" to='/'>Revenir</Link></p>
                    </div>

                    <h1 className="whiteSpecialTitle">Rencontre love</h1>

                    <div className="boxRounded dark-shadow">
                        {/* eslint-disable-next-line */}
                        <div
                            style={{
                                position: 'absolute',
                                top: 10,
                                left: 10,
                                border: '1px solid white',
                                borderRadius: '100%'
                            }}
                            onClick={this.setReturn.bind(this)}
                            role="button"
                            tabIndex="0"
                        >
                            <IconButton aria-label="delete" size="small" >
                                <ArrowBack fontSize="small" />
                            </IconButton>
                        </div>
                        <div
                            style={{
                                width: '100%',
                                height: 30
                            }}
                        >
                        </div>
                        <h2 className="whiteSecondTitle centeredText">
                          Ma Position GPS
                        </h2>
                        <div>
                            <p>
                                Perle rencontre essaie de géolocalisation votre position
                            </p>
                            <div
                                style={{ height: diametre, width: diametre }}>
                                <div
                                    style={{
                                        height: diametre - padding,
                                        width: diametre - padding
                                    }}
                                    className='circleLocation'
                                >
                                    <div
                                        style={{
                                            height: diametre - (2 * padding),
                                            width: diametre - (2 * padding)
                                        }}
                                        className='circleLocation'
                                    >
                                        <div
                                            style={{
                                                height: diametre - (3 * padding),
                                                width: diametre - (3 * padding)
                                            }}
                                            className='circleLocation'
                                        >
                                            <div>
                                                <LocationOn size={35}/>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <Button
                                variant="contained"
                                color="secondary"
                                size="small"
                                style={{ borderRadius: 30 }}
                            >
                                Google
                            </Button>
                        </div>
                    </div>
                </ThemeProvider>
            </div>
        )
    }
}

export default Geolocalisation
