import React from 'react'
import BoxActif from './layouts/boxActif/BoxActif'
import accountAvatar from './statics/images/accountAvatar.png'
import {
    Modal 
} from '@material-ui/core'

class Aside extends React.Component{
    constructor (props) {
        super(props)
        this.state = {
            open: false,

        }
    }
    deconnect = () => {
        localStorage.removeItem('Token')
        this.props.history.push('/Authentification=Connexion')
    }
    handleClose = () => {
        this.setState({open:false})
    }

    render () {
        // const { datas } = props
        return (
            <div className='Aside'>
                <aside>
                    <div className="account">
                        <h1 className="simpleTitle">Mon profil</h1>
                        <div className="accountContent">
                            <p className="accountAvatar">
                                <img src={accountAvatar} alt='accountAvatar' onClick={() => alert('show profil details')} />
                            </p>
                            <ul className="accountDescription">
                                <li className="accountName">{this.props.datas}</li>
                                {/* eslint-disable-next-line */}
                                <li
                                    style={{ cursor: 'pointer' }}
                                    onClick={() =>this.deconnect()}
                                >Deconnexion</li>
                            </ul>
                        </div>
                    </div>

                    <div className="profileActifs">
                        <h1 className="simpleTitle">Liste des actifs</h1>
                        <BoxActif/>
                        <BoxActif/>
                        <BoxActif/>
                        <BoxActif/>
                        <div className="btnMoreActifs">
                            <p>Voir plus</p>
                        </div>
                    </div>
                </aside>
                <div>
                <div className="boxProfileActif">
                    {/* <img src={profilMini} alt="profil-mini" /> */}
                    <div className="detailProfilActif">
                        <p className="pseudoProfilActif">Profil details</p>
                        <input
                                            style={{ cursor: 'pointer' }}
                                            className="max-width btn button-light-blue"
                                            type="button"
                                            value={'test' }
                                            onClick={()=>this.setState({open:true})}
                                        />
                    </div>
                </div>
                </div>
                <Modal
                open={this.state.open}
                onClose={() =>this.handleClose()}
                aria-labelledby="simple-modal-title"
                aria-describedby="simple-modal-description"
                >
               <div>
                   <p>modal body</p>
               </div>
                </Modal>




            </div>
        )

    }

}


export default Aside
