import React, { Component } from 'react'
import Block from './layouts/block/Block'
import Header from './layouts/header/Header'
import './statics/styles/Chat.scss'
import Moment from 'react-moment'
import ChatInput from './layouts/chatInput/ChatInput'
import Notification from './layouts/notifications/Notification'

const datas = [
    { id: 1, notification: 0, url: 'https://www.village-justice.com/articles/local/cache-gd2/61/b91c0bc706676d96e362711536df5b.jpg?1603207633' },
    { id: 2, notification: 0, url: 'https://wl-sympa.cf.tsp.li/resize/728x/jpg/f6e/ef6/b5b68253409b796f61f6ecd1f1.jpg' },
    { id: 3, notification: 2, url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcS9e3tHfGvC1Z9xPpa_bU8ynCuWg_rn-C1YBQ&usqp=CAU' },
    { id: 4, notification: 0, url: 'https://images-na.ssl-images-amazon.com/images/I/81lKUxkmF5L._SY600_.jpg' },
    { id: 5, notification: 0, url: 'https://www.fourchette-et-bikini.fr/sites/default/files/styles/full_670x350/public/femme_amoureuse.jpg?itok=ovXsCBSe' }
]

const sms = [
    {
        utilisateurs_1_id: 1,
        utilisateurs_2_id: 4,
        messages_contenue: 'Coucou',
        type: 'perle',
        updated_at: '2010-10-29T06:30:03.000000Z',
        created_at: '2010-10-29T06:30:03.000000Z',
        id: 1
    },
    {
        utilisateurs_1_id: 4,
        utilisateurs_2_id: 1,
        messages_contenue: 'Coucou',
        type: 'perle',
        updated_at: '2010-10-29T09:30:03.000000Z',
        created_at: '2010-10-29T09:30:03.000000Z',
        id: 3
    },
    {
        utilisateurs_1_id: 4,
        utilisateurs_2_id: 1,
        messages_contenue: 'Coucou',
        type: 'perle',
        updated_at: '2010-10-29T08:30:03.000000Z',
        created_at: '2010-10-29T08:30:03.000000Z',
        id: 2
    },
    {
        utilisateurs_1_id: 1,
        utilisateurs_2_id: 4,
        messages_contenue: 'Coucou',
        type: 'perle',
        updated_at: '2010-10-29T11:30:03.000000Z',
        created_at: '2010-10-29T11:30:03.000000Z',
        id: 4
    }
]

function HeaderType ({ title, date }) {
    return (
        <div className='HeaderType'>
            <div className='lineH'></div>
            <div className='center'>
                <h1>{ title }</h1>
                <Moment format="dddd DD MMMM YYYY">
                    { date }
                </Moment>
            </div>
            <div className='lineH'></div>
        </div>
    )
}

class Chat extends Component {
    constructor (props) {
        super(props)
        this.state = {
            showNotification: false,
            datas: [

            ]
        }
    }

    toogleShowNotification () {
        this.setState({ showNotification: !this.state.showNotification })
    }

    userContacter (user) {
        console.log({ user })
    }

    sortData (datas, key) {
        return datas.sort((a, b) => {
            return a[key] - b[key]
        })
    }

    render () {
        const Rencontre = () => <HeaderType title='LA RENCONTRE' date={new Date()}/>
        const Decouvert = () => <HeaderType title='LA DECOUVERTE' date={new Date()}/>
        const RendezVous = () => <HeaderType title='LE RENDEZ-VOUS' date={new Date()}/>
        const {
            showNotification
        } = this.state
        const dats = this.sortData(sms, 'id')

        const isRecontre = true
        return (
            <div className='Chat'>
                {
                    showNotification && <Notification toogleShowNotification={this.toogleShowNotification.bind(this)}/>
                }
                <div className='container'>
                    <Header userContacter={this.userContacter.bind(this)} />
                    {
                        dats && dats.map((e, i) => {
                            if (isRecontre) {
                                return <>
                                    {i === 0 && <Rencontre/>}
                                    <Block
                                        typeKey={false}
                                        isLeft={false}
                                        url={datas[1].url}
                                        text='Bonjour en chante Veronique !'
                                    />

                                    </>
                            }
                        })
                    }
                    <Rencontre/>
                    <div>
                        <Block
                            typeKey={false}
                            isLeft={false}
                            url={datas[1].url}
                            text='Bonjour en chante Veronique !'
                        />
                        <Block
                            typeKey={false}
                            isLeft={true}
                            url={datas[4].url}
                            text='Bonjour jeff, '
                        />
                        <Block
                            typeKey={false}
                            isLeft={false}
                            url={datas[1].url}
                            text='Bonjour en chante Veronique'
                        />
                        <Block
                            typeKey={false}
                            isLeft={true}
                            url={datas[4].url}
                            text="Lorem Ipsum is simply dummy text of the printing and typesetting industry.
                        Lorem Ipsum has been the industry's standard dummy text ever since the 1500s,
                        when an unknown printer took a galley of type and scrambled it to make a type specimen book."
                        />
                        <Decouvert/>
                        <Block
                            typeKey={false}
                            isLeft={false}
                            url={datas[1].url}
                            text='Bonjour en chante Veronique'
                        />
                        <Block
                            typeKey={false}
                            isLeft={true}
                            url={datas[4].url}
                            text='Bonjour jeff, '
                        />
                        <Block
                            typeKey={false}
                            isLeft={false}
                            url={datas[1].url}
                            text='Bonjour en chante Veronique'
                        />
                        <Block
                            typeKey={false}
                            isLeft={true}
                            url={datas[4].url}
                            text='Bonjour jeff, '
                        />
                        <RendezVous/>
                        <Block
                            typeKey={false}
                            isLeft={false}
                            url={datas[1].url}
                            text='Bonjour en chante Veronique'
                        />
                        <Block
                            typeKey={false}
                            isLeft={true}
                            url={datas[4].url}
                            text='Bonjour jeff, '
                        />
                        <Block
                            typeKey={false}
                            isLeft={false}
                            url={datas[1].url}
                            text='Bonjour en chante Veronique'
                        />
                        <Block
                            typeKey={false}
                            isLeft={true}
                            url={datas[4].url}
                            text='Bonjour jeff, '
                        />
                        <Block
                            typeKey={true}
                            isLeft={true}
                            url={datas[4].url}
                            text='Bonjour jeff, '
                        />
                    </div>
                    <ChatInput/>
                </div>
            </div>
        )
    }
}

export default Chat
