import React, { Component } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import {
    TextField,
    Input,
    IconButton,
    InputAdornment,
    Button,
    Grid
} from '@material-ui/core'
import {
    createMuiTheme,
    ThemeProvider
} from '@material-ui/core/styles'

import {
    Visibility,
    VisibilityOff,
    Person,
    Https
} from '@material-ui/icons'

import Facebook from './statics/images/Facebook.png'
import Google from './statics/images/Google.png'

const theme = createMuiTheme({
    palette: {
        type: 'dark'
    }
})

class Connexion extends Component {
    constructor (props) {
        super(props)
        this.state = {
            name: '',
            password: '',
            error: false,
            isLoading: false,
            isShow: false
        }
    }

    onSubmit (e) {
        e.preventDefault()
        const {
            name,
            password
        } = this.state
        if (name !== '' && password !== '') {
            this.setState({ isLoading: true })
            axios
                .post('/api/login', {
                    name,
                    password
                })
                .then(async ({ data: { token } }) => {
                    this.setState({ isLoading: false })
                    await localStorage.setItem('Token', JSON.stringify(token))
                    this.props.history.push('/Profiles')
                })
                .catch(e => {
                    this.setState({ isLoading: false, error: true })
                })
        }
    }

    onChangeInput (e) {
        this.setState({ [e.target.name]: e.target.value, error: false })
    }

    render () {
        const {
            error,
            isLoading,
            name,
            password,
            isShow
        } = this.state
        return (
            <div className='Connection'>
                <ThemeProvider theme={theme}>
                    <div className="parent-space-between">
                        <p><Link className="backward-round" to='/'>Revenir</Link></p>
                    </div>

                    <h1 className="whiteSpecialTitle">Rencontre love</h1>

                    <div className="boxRounded dark-shadow">
                        <h2 className="whiteSecondTitle centeredText">Connexion</h2>
                        <div
                            className="inline"
                            style={{
                                display: 'flex',
                                justifyContent: 'space-between'
                            }}
                        >
                            <Button
                                variant="contained"
                                color="primary"
                                size="small"
                                style={{ borderRadius: 30 }}
                                startIcon={<img src={Facebook} alt='facebook' style={{ width: 20, height: 20 }} />}
                            >
                                  Facebook
                            </Button>
                            <Button
                                variant="contained"
                                color="secondary"
                                size="small"
                                style={{ borderRadius: 30 }}
                                startIcon={ <img src={Google} alt='google' style={{ width: 20, height: 20 }}/> }
                            >
                                  Google
                            </Button>
                        </div>
                        <h2 style={{ color: '#afb0b2', marginTop: 20 }}>- ou -</h2>
                        <form className="margin-tb-20" >


                                <li>
                                    <Grid container spacing={1} alignItems="flex-end">
                                        <Grid item>
                                            <Person />
                                        </Grid>
                                        <Grid item>
                                            <TextField
                                                label="Pseudo *"
                                                value={name}
                                                error={error}
                                                onChange={this.onChangeInput.bind(this)}
                                                // className="max-width"
                                                name='name'
                                                style={{ width: 215 }}
                                            />
                                        </Grid>
                                    </Grid>
                                </li>
                                <li>
                                    <Grid container spacing={1} alignItems="flex-end">
                                        <Grid item>
                                            <Https />
                                        </Grid>
                                        <Grid item>
                                            <TextField
                                                label="Mot de passe *"
                                                type="password"
                                                value={password}
                                                error={error}
                                                onChange={this.onChangeInput.bind(this)}
                                                name='password'
                                                style={{ width: 215 }}
                                            />
                                        </Grid>
                                    </Grid>
                                </li>


                            { /* Button */ }
                            <ul className="standar-vertic-spacing">
                                <li>
                                    <input
                                        style={{ cursor: 'pointer' }}
                                        className="max-width btn button-light-blue"
                                        type="button"
                                        value={ isLoading ? 'Loading...' : 'Connexion' }
                                        onClick={this.onSubmit.bind(this)}
                                    />
                                </li>
                                <li>
                                    <Link to="/Authentification=Inscription">
                                        <input
                                            style={{ cursor: 'pointer' }}
                                            className="max-width btn btn-light-border-only"
                                            type="button"
                                            value="Inscription"
                                        />
                                    </Link>
                                </li>
                            </ul>
                        </form>
                    </div>
                </ThemeProvider>
            </div>
        )
    }
}

export default Connexion
