import React from 'react'
import Badge from '@material-ui/core/Badge'
import Avatar from '@material-ui/core/Avatar'
import { makeStyles } from '@material-ui/core/styles'
import './statics/styles/Header.scss'

import {
    ArrowBack,
    MoreVert
} from '@material-ui/icons'

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1)
        }
    }
}))

function BadgeAvatars ({ user, notification }) {
    const classes = useStyles()

    return (
        <div className={classes.root}>
            <Badge
                overlap="circle"
                anchorOrigin={{
                    vertical: 'top',
                    horizontal: 'right'
                }}
                badgeContent={notification !== 0 && <div className='badge'>2</div>}
            >
                <Avatar
                    style={{ width: user ? 50 : 40, height: user ? 50 : 40 }}
                    alt="Travis Howard"
                    src="https://images-na.ssl-images-amazon.com/images/I/81lKUxkmF5L._SY600_.jpg"
                />
            </Badge>
        </div>
    )
}

function Header () {
    return (
        <div className='Header'>
            <div>
                <ArrowBack  style={{ color: 'white' }} />
            </div>
            <div>
                <BadgeAvatars user={false} notification={0} />
            </div>
            <div>
                <BadgeAvatars user={false} notification={0} />
            </div>
            <div>
                <BadgeAvatars user={false} notification={0} />
            </div>
            <div className='veronique'>
                <h1>Veronique</h1>
                <BadgeAvatars user={true} notification={0} />
            </div>
            <div>
                <BadgeAvatars user={false} notification={2} />
            </div>
            <div>
                <BadgeAvatars user={false} notification={0} />
            </div>
            <div>
                <BadgeAvatars user={false} notification={0} />
            </div>
            <div>
                <MoreVert  style={{ color: 'white' }} />
            </div>
        </div>
    )
}

export default Header
