import React, { useState, useEffect } from 'react'
import Block from './layouts/block/Block'
import CompetencesDetail from './layouts/competencesDetail/CompetencesDetail'
import './statics/css/Competences.scss'
import techIcon from './statics/images/icon.jpeg'

function Competences () {
  const [datas, setDatas] = useState([])
  const [active, setActive] = useState(0)

  useEffect(() => {
    const data = [
      {
        id: 0,
        title: 'Site et application',
        value: 50,
        detail: {
          competence: 'Nous sommes dans la divers domaine adapter aux besoins de nos clients',
          title: 'Site vitrine et application web',
          detail: 'Nous sommes dans la divers domaine adapter aux besoins de nos clients',
          techs: [
            techIcon,
            techIcon,
            techIcon
          ]
        }
      },
      {
        id: 1,
        title: 'Application bureau',
        value: 10,
        detail: {
          competence: 'Nous sommes dans la divers domaine adapter aux besoins de nos clients',
          title: 'Site vitrine et application web',
          detail: 'Nous sommes dans la divers domaine adapter aux besoins de nos clients',
          techs: [
            techIcon
          ]
        }
      },
      {
        id: 2,
        title: 'Application mobile',
        value: 10,
        detail: {
          competence: 'Nous sommes dans la divers domaine adapter aux besoins de nos clients',
          title: 'Site vitrine et application web',
          detail: 'Nous sommes dans la divers domaine adapter aux besoins de nos clients',
          techs: [
            techIcon,
            techIcon
          ]
        }
      },
      {
        id: 3,
        title: 'Design interface',
        value: 30,
        detail: {
          competence: 'Nous sommes dans la divers domaine adapter aux besoins de nos clients',
          title: 'Site vitrine et application web',
          detail: 'Nous sommes dans la divers domaine adapter aux besoins de nos clients',
          techs: [
            techIcon,
            techIcon
          ]
        }

      }
    ]
    setDatas(data)
    setActive(data[0].id)
  }, [])

  const onHover = (id) => {
    console.log(id)
    setActive(id)
  }

  return (
    <div className="Competences">
      {
            datas?.map(({ title, value, id, detail }) => {
              return <div
                key={id}
                className="CompetencesBlock"
              >
                <Block
                  { ...{ title, value, onHover, id }}
                />
                <div className="CompetencesDetails">
                  {
                    active === id &&
                      <CompetencesDetail
                        { ...{ detail }}
                      />
                  }
                </div>
              </div>
            })
      }
    </div>
  )
}

export default Competences
