        id: 5
    },
    {
        utilisateurs_1_id: 4,
        utilisateurs_2_id: 2,
        messages_contenue: 'Quoi de neuf???',
        type: 'perle',
        updated_at: '2010-10-29T09:30:03.000000Z',
        created_at: '2010-10-29T09:30:03.000000Z',
        id: 6
    },
    {
        utilisateurs_1_id: 2,
        utilisateurs_2_id: 4,
        messages_contenue: 'Bonjour Jeff!',
        type: 'perle',
        updated_at: '2010-10-29T08:30:03.000000Z',
        created_at: '2010-10-29T08:30:03.000000Z',
        id: 7
    },
    {
