        id: 10
    }
]

function HeaderType ({ title, date }) {
    return (
        <div className='HeaderType'>
            <div className='lineH'></div>
            <div className='center'>
                <h1>{ title }</h1>
                <Moment format="dddd DD MMMM YYYY">
                    { date }
                </Moment>
            </div>
            <div className='lineH'></div>
        </div>
    )
}

class Chat extends Component {
    constructor (props) {
        super(props)
        this.state = {
            showNotification: false,
            datas: [],
            userContacter: null,
            listUser: [],
            userActif: {
                id: 2,
                notification: 0,
                url: 'https://images-na.ssl-images-amazon.com/images/I/81lKUxkmF5L._SY600_.jpg'
            }

        }
        this.urlconv = '/api/get_messages'
        this.socket = socketIOClient('https://evening-basin-72730.herokuapp.com/')
    }

    toogleShowNotification () {
        this.setState({ showNotification: !this.state.showNotification })
    }

    getUserConnect () {
        const Authorization = 'Bearer ' + JSON.parse(localStorage.getItem('Token'))
        axios
            .get('/api/profil', {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: Authorization
                }
            })
            .then(({ data }) => {
                console.log('response: ', data)
            })
            .catch(error => console.log({ error }))
    }

    componentDidMount () {
        this.setState({ datas: sms, listUser })
        this.getUserConnect()
    }

    setListen () {
        const { userContacter, userActif } = this.state
        const eventName = 'SendMessage_from' + userActif + 'to' + userContacter

        this.socket.on(eventName, ({ data }) => {
            console.log(data)
            this.setState((prevState) => {
                return { datas: data }
            })
        })
    }

    userContacter (userContacter) {
        if (localStorage.hasOwnProperty('Token')) {
            axios.post(this.urlconv, {
                user_1: 1,
                user_2: 2,
                type: 'bourse'
            }, {
                headers: {
                    Authorization: 'Bearer ' + JSON.parse(localStorage.getItem('Token'))
                }
            })
                .then(({ data }) => {
                    console.log({ data })
                    this.setState({ datas: data })
                })
                .catch(error => console.log({ error }))
        } else {
            console.log('Token not found')
        }
        this.setState({ userContacter })
    }

    sortData (datas, key) {
        return Object.entries(datas).map(([key, values]) => values).sort((a, b) => {
            return a[key] - b[key]
        })
    }

    onSubmit (value) {
        const { userContacter, userActif } = this.state
        // const stock = {
        //     utilisateurs_1_id: userContacter,
        //     utilisateurs_2_id: userActif.id,
        //     messages_contenue: value,
        //     type: 'perle',
        //     updated_at: '2010-10-29T11:30:03.000000Z',
        //     created_at: '2010-10-29T11:30:03.000000Z',
        //     id: 8
        // }
        const payload = {
            user_1: userActif,
            user_2: userContacter,
            message: value
        }
        this.socket.emit('recevePayload', payload)

        // this.setState({ datas: [...this.state.datas, stock] })
    }

    findOne (data, key, search) {
        return data.filter(e => {
            return e[key] === search
        })
    }

    // filterData (id1, id2) {
    //     return this.state.datas.filter(({ utilisateurs_1_id, utilisateurs_2_id }) => {
    //         return utilisateurs_1_id === id1 && utilisateurs_2_id == id2 || utilisateurs_1_id === id2 && utilisateurs_2_id == id1
    //     })
    // }

  OnChangeLoginInputImage = (e) => {
        let files = e.target.files;
        let reader = new FileReader();
        reader.readAsDataURL(files[0]);
        reader.onload = (e) => {
          // const formData = {file: e.target.result
          this.setState({ImageChambre: e.target.result})
        }
      }


    render () {
        const Rencontre = () => <HeaderType title='LA RENCONTRE' date={new Date()}/>
        const Decouvert = () => <HeaderType title='LA DECOUVERTE' date={new Date()}/>
        const RendezVous = () => <HeaderType title='LE RENDEZ-VOUS' date={new Date()}/>
        const {
            showNotification,
            listUser,
            datas,
            userActif
        } = this.state
        const dats = this.sortData(datas, 'id')

        let init = 0
        let us = 0
        let one = false

        const lenDecouvert = 5
        const lenRendezVous = 6
        return (
            <div className='Chat'>
                {
                    showNotification && <Notification toogleShowNotification={this.toogleShowNotification.bind(this)}/>
                }
                <div className='container'>
                    <Header userContacter={this.userContacter.bind(this)} />
                    {
                        dats && dats.map((e, i) => {
                            one = false
                            if (us !== e.utilisateurs_1_id) {
                                init = init + 1
                                us = e.utilisateurs_1_id
                                if ((init === lenDecouvert || init === lenRendezVous) && dats.length !== i + 1) {
                                    one = true
                                }
                            }
                            let urlImage = this.findOne(listUser, 'id', e.utilisateurs_2_id)[0]
                            if (urlImage) {
                                urlImage = urlImage.url
                            }
                            return <div key={i}>
                                { i === 0 && <Rencontre/> }
                                <Block
                                    typeKey={false}
                                    isLeft={e.utilisateurs_1_id === userActif.id}
                                    url={urlImage}
                                    text={e.messages_contenue}
                                />
                                { init === lenDecouvert && one && <Decouvert/> }
                                { init === lenRendezVous && one && <RendezVous/> }
                            </div>
                        })
                    }
                    <ChatInput onSubmit={this.onSubmit.bind(this)}/>
                </div>
            </div>
        )
    }
}

export default Chat
